mapper.select:
SELECT s.stu_id stuId, s.stu_num stuNum, s.stu_period stuPeriod, s.stu_name stuName, s.stu_class stuClass, s.stu_tel stuTel, spr.stu_practice_name stuPracticeName, COALESCE (spr.score,'') score, spr.practice_id practiceId, su.user_name teaName, CASE spr.status WHEN '00' THEN '已通过' WHEN '01' THEN '未通过' ELSE '未录入' END AS status, cp.practice_name practiceName, cp.practice_number practiceNumber FROM stu_user s LEFT JOIN stu_practice_rel spr ON s.stu_id = spr.stu_id LEFT JOIN tea_stu_rel ts ON s.stu_id = ts.stu_id and ts.practice_id = spr.practice_id LEFT JOIN course_practice cp ON spr.practice_id = cp.practice_id LEFT JOIN sys_user su ON su.user_id = ts.tea_id WHERE ts.STATUS = '01'
AND ts.tea_id = #{teaId}
AND su.user_name like concat('%', #{teaName}, '%')
AND ts.practice_id = #{practiceId}
AND ts.stu_id = #{stuId}
AND s.stu_period like concat('%', #{stuPeriod}, '%')
AND cp.practice_name like concat('%', #{practiceName}, '%')
AND s.stu_name like concat('%', #{stuName}, '%')
AND s.stu_num like concat('%', #{stuNum}, '%')
ORDER BY s.stu_id

mapper.update:
UPDATE tea_stu_rel
SET STATUS = #{status}
WHERE
	stu_id = #{stuId}
	AND tea_id = #{teaId};

UPDATE stu_practice_rel
SET score = #{score}
WHERE
	stu_id = #{stuId}
	AND practice_id = #{practiceId}

UPDATE stu_practice_rel
SET stu_practice_name = #{stuPracticeName},
	status = #{status}
WHERE
	stu_id = #{stuId}
	AND practice_id = #{practiceId}
